parameters.jsCode:
// Get the raw content from the assistant's message
const rawContent = $json.message?.content;

if (!rawContent) {
  return {
    error: 'No content found in message.content'
  };
}

// Clean up and parse the stringified JSON
let parsed;
try {
  const cleaned = rawContent.replace(/\\n/g, '') // remove escaped newlines
                            .replace(/^```json|```$/g, '') // strip code block wrappers
                            .trim();

  parsed = JSON.parse(cleaned);
} catch (err) {
  return {
    error: 'Failed to parse JSON content',
    raw: rawContent
  };
}

// Return parsed object cleanly
return parsed;

const vtt = $json.body; // This is where the VTT text is expected

if (!vtt) {
	return { transcript: null, note: "no TikTok captions" };
}

const lines = vtt
	.split(/\r?\n/) // Split into lines
	.filter(l => l && !l.match(/^(\d+|WEBVTT|X-TIMESTAMP|[\d:\.]+\s+-->|\s*$)/)); // Remove timestamps & metadata

return {
	transcript: lines.join(" ")
};

