parameters.jsCode:
const transcript = $json.transcript || '';

function isEnglish(text) {
  const englishChars = /[a-zA-Z]/;
  const nonEnglishChars = /[^\x00-\x7F]/;
  return englishChars.test(text) && !nonEnglishChars.test(text);
}

function hasEnoughLines(text) {
  // Count number of lines (split by period or newline)
  const lines = text.trim().split(/\n|\./).filter(line => line.trim().length > 10); // optional: ignore short fragments
  return lines.length >= 2;
}

// Apply both filters
if (!transcript || !isEnglish(transcript) || !hasEnoughLines(transcript)) {
  return {}; // <-- IMPORTANT: Return empty object to avoid breaking 'Run Once for Each Item'
}

return {
  json: {
    transcript,
    note: "✅ English & multi-line transcript"
  }
};

