const startTimeStr = $json.calls?.[0]?.start_time;

if (!startTimeStr) {
  return {
    json: {
      last_call_date: null,
      error: "Missing start_time in calls[0]"
    }
  };
}

try {
  const startDate = new Date(startTimeStr);
  if (isNaN(startDate.getTime())) {
    throw new Error("Invalid start_time format");
  }

  const year = startDate.getUTCFullYear();
  const month = startDate.getUTCMonth();
  const day = startDate.getUTCDate();

  // Colombian midnight = 00:00 local = 05:00 UTC
  const colombiaMidnightUTC = new Date(Date.UTC(year, month, day, 5, 0, 0));
  const timestamp = colombiaMidnightUTC.getTime();

  return {
    json: {
      last_call_date: timestamp
    }
  };
} catch (err) {
  return {
    json: {
      last_call_date: null,
      error: err.message
    }
  };
}

// Get last call timestamp from "Last Call Status" field
const lastCallDateRaw = $json['Last Call Status'];

// Convert it to a Date object
const lastCallDate = new Date(lastCallDateRaw);

// Get current date (in system timezone)
const today = new Date();

// Calculate difference in full days
const msPerDay = 1000 * 60 * 60 * 24;
const daysDiff = Math.ceil((today - lastCallDate) / msPerDay);

// Check if the call was already made today
const lastCallStr = lastCallDate.toDateString();
const todayStr = today.toDateString();
const alreadyCalledToday = (lastCallStr === todayStr);

// Return results
return {
  json: {
    days_since_last_call: daysDiff,
    already_called_today: alreadyCalledToday,
    last_call_date: lastCallDateRaw
  }
};

