// --- CONFIG ---
const defaultYear = 2025;
const defaultMinutes = 0;
const timezoneOffsetMinutes = -330; // IST (UTC+5:30) â†’ offset = -330

// --- INPUT ---
const rawDate = ($input.first().json.query.bookingDate || '').trim();
const rawTime = ($input.first().json.query.bookingTime || '').trim();

// --- CLEANUP ---
function fixTypos(str) {
  return str
    .toLowerCase()
    .replace(/augustust/g, 'august')
    .replace(/septemberember/g, 'september')
    .replace(/ +/g, ' ')
    .trim();
}

const cleanDate = fixTypos(rawDate);
const cleanTime = fixTypos(rawTime);

// --- PARSE ---
const months = {
  january: 0, february: 1, march: 2, april: 3, may: 4, june: 5,
  july: 6, august: 7, september: 8, october: 9, november: 10, december: 11
};

// Extract day and month
const dateMatch = cleanDate.match(/(\d{1,2})\s+([a-z]+)/i);
if (!dateMatch) throw new Error(`âŒ Could not parse date: "${cleanDate}"`);
const [, dayStr, monthStr] = dateMatch;
const day = parseInt(dayStr, 10);
const month = months[monthStr.toLowerCase()];
if (month === undefined) throw new Error(`âŒ Unknown month: "${monthStr}"`);

// Extract hour and AM/PM
const timeMatch = cleanTime.match(/(\d{1,2})(?::(\d{2}))?\s*(am|pm)?/i);
if (!timeMatch) throw new Error(`âŒ Could not parse time: "${cleanTime}"`);
let hour = parseInt(timeMatch[1], 10);
let minutes = timeMatch[2] ? parseInt(timeMatch[2], 10) : defaultMinutes;
const ampm = (timeMatch[3] || '').toLowerCase();

if (ampm === 'pm' && hour < 12) hour += 12;
if (ampm === 'am' && hour === 12) hour = 0;

// --- CONSTRUCT DATE IN UTC ---
const localDate = new Date(Date.UTC(defaultYear, month, day, hour, minutes));
const utcDate = new Date(localDate.getTime() - timezoneOffsetMinutes * 60 * 1000);
const endDate = new Date(utcDate.getTime() + 30 * 60 * 1000);

// --- OUTPUT ---
return [
  {
    json: {
      rawInput: `${cleanDate} ${cleanTime}`,
      start_time: utcDate.toISOString(),
      end_time: endDate.toISOString(),
      date: localDate.toISOString().split('T')[0], // show original local date
      time: `${String(hour).padStart(2, '0')}:${String(minutes).padStart(2, '0')}`
    }
  }
];
