// Get the XML content from the previous node
const xmlContent = $input.first().json.data || $input.first().json;

try {
  // Simple regex parsing to extract episodes
  const episodes = [];

  // Find all <item> blocks (episodes)
  const itemMatches = xmlContent.match(/<item[^>]*>([\s\S]*?)<\/item>/gi);

  if (!itemMatches) {
    throw new Error('No episodes found in RSS feed');
  }

  for (const itemContent of itemMatches) {
    const episode = {};

    // Extract title
    const titleMatch = itemContent.match(/<title[^>]*>([\s\S]*?)<\/title>/i);
    if (titleMatch) {
      episode.title = titleMatch[1].replace(/<!?\[CDATA\[|\]\]>/g, '').trim();
    }

    // Extract audio URL from enclosure
    const enclosureMatch = itemContent.match(/<enclosure[^>]*url=["']([^"']*)["'][^>]*>/i);
    if (enclosureMatch) {
      episode.audioUrl = enclosureMatch[1];
    }

    // Extract description
    const descMatch = itemContent.match(/<description[^>]*>([\s\S]*?)<\/description>/i);
    if (descMatch) {
      episode.description = descMatch[1].replace(/<!?\[CDATA\[|\]\]>/g, '').replace(/<[^>]*>/g, '').trim();
    }

    // Only add episodes that have both title and audio URL
    if (episode.title && episode.audioUrl) {
      episodes.push(episode);
    }
  }

  return [{ json: { episodes: episodes } }];

} catch (error) {
  return [{ json: { error: error.message, rawXML: xmlContent } }];
}

// Get the parsed RSS data
const data = $input.first().json;

// Check if we have episodes
if (!data.episodes || !Array.isArray(data.episodes)) {
  return [{ json: { error: "No episodes found in RSS feed" } }];
}

// Get the episode title slug from Extract Input
const episodeTitleSlug = $('Extract Input2').first().json.episodeTitleSlug;

// Convert slug to searchable terms
// "ceo-diaries-the-mistake-that-nearly-derailed-mrbeasts" becomes search terms
let searchTerms = [];
if (episodeTitleSlug) {
  searchTerms = episodeTitleSlug.split('-').filter(term => term.length > 2);
}

// Find episode that matches the most search terms
let bestMatch = data.episodes[0]; // fallback to latest
let maxMatches = 0;

for (const episode of data.episodes) {
  const title = episode.title.toLowerCase();
  let matches = 0;

  for (const term of searchTerms) {
    if (title.includes(term.toLowerCase())) {
      matches++;
    }
  }

  if (matches > maxMatches) {
    maxMatches = matches;
    bestMatch = episode;
  }
}

return [{
  json: {
    title: bestMatch.title,
    audioUrl: bestMatch.audioUrl,
    description: bestMatch.description || '',
    searchTerms: searchTerms,
    matchScore: maxMatches
  }
}];

// Get the analysis data
const analysisData = $input.first().json;
const extractData = $('Extract Input2').first().json;
const episodeData = $('Extract Episodes').first().json;

// Simple formatting - convert markdown to HTML
let formattedAnalysis = analysisData.businessAnalysis
  .replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>')  // Bold
  .replace(/### (.*?)(\n|$)/g, '<h3>$1</h3>')       // H3 headers
  .replace(/## (.*?)(\n|$)/g, '<h2>$1</h2>')        // H2 headers  
  .replace(/^- (.*?)$/gm, '<li>$1</li>')            // List items
  .replace(/(\n|^)([A-Z][^:\n]*):(\n|$)/g, '<h4>$2</h4>') // Headers with colons
  .replace(/\n\n/g, '</p><p>')                      // Paragraphs
  .replace(/\n/g, '<br>');                          // Line breaks

// Wrap lists in UL tags
formattedAnalysis = formattedAnalysis.replace(/(<li>.*?<\/li>)/gs, '<ul>$1</ul>');
formattedAnalysis = '<p>' + formattedAnalysis + '</p>';

const htmlReport = `
