return [{
  json: {
    htmlReport: htmlReport,
    episodeTitle: episodeData.title,
    userEmail: extractData.userEmail
  }
}];

const formData = $input.first().json;
const url = formData["Podcast URL"] || '';

// Extract episode ID (after ?i=)
let episodeId = null;
if (url.includes('?i=')) {
  episodeId = url.split('?i=')[1];
  if (episodeId.includes('&')) {
    episodeId = episodeId.split('&')[0];
  }
}

// Extract podcast ID (after /id)
let podcastId = null;
const idMatch = url.match(/\/id(\d+)/);
if (idMatch) {
  podcastId = idMatch[1];
}

// FIXED: Extract episode title slug from URL
let episodeTitleSlug = null;
// URL format: .../podcast/EPISODE-SLUG/id123...
const episodeSlugMatch = url.match(/\/podcast\/([^\/]+)\/id\d+/);
if (episodeSlugMatch) {
  episodeTitleSlug = episodeSlugMatch[1];
}

return [{
  json: {
    podcastInput: url,
    userEmail: formData["Email to send report"],
    episodeId: episodeId,
    podcastId: podcastId,
    episodeTitleSlug: episodeTitleSlug,
    episodeLimit: 1
  }
}];

